mapper.select:
select creator_num
from creator
where creator=#{id}

select creator_name
from creator join member on(member_id = creator)
where member_id = #{id}

select member_id
from member join creator on(member_id = creator)
where creator_num=#{creatorNum}

select *
from funding
where funding_num=#{bId}

select *
from Image join funding on(funding_num = image_board_id)
where image_board_id=#{bId} and image_board_cate=1

mapper.update:
update funding
set funding_count = funding_count+1
where funding_num=#{bId}

mapper.select:
select creator
from funding join creator using(creator_num)
where funding_num=#{bId}

select *
from funding_notice
where funding_num=#{bId}

select count(*)
from funding_notice
where funding_num=#{bId}

select creator_num
from creator
where creator=#{writer}

select *
from funding_notice
where funding_notice_writer=#{writer}

select *
from funding
where funding_num=#{bId}

select *
from reward
where funding_num=#{bId}

select *
from funding
where funding_num=#{bId}

select *
from payschedule
where funding_num=#{fundingNum}

select count(*)
from payschedule
where funding_num=#{fundingNum}

select count(*)
from review
where funding_num=#{fundingNum}

		select member_nickname, member_id
		from member join review on(member_id=reviewer)
        where funding_num=#{fundingNum}

select count(*)
from dibs
where funding_num=#{bId}

select *
from dibs
where funding_num=#{bId}

mapper.update:
update funding
set confirm='E'
where funding_num=#{fundingNum}

mapper.select:
select *
from member
where member_nickname=#{buyerName}

select creator
from creator
where creator_name=#{creatorNickname}

select *
from follow
where creator_num=#{creatorNum}

select count(*)
from follow
where creator_num=#{creatorNum}
